package arrayprogramms;

public class Multiplication {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=10;i++)
		{
			for(int j=1;j<=10;j++)
			{
				
				//System.out.printf("%2d * %2d = %3d \t",j,i,j*i);
				//System.out.println();
				String s=j+"*"+ i+"="+(i *j);
				System.out.print(s+"\t");
				
			
			}
			System.out.println();
		
		}

	
	}

}
